import telebotkeyboard1 = telebot.types.ReplyKeyboardMarkup()keyboard1.row('Привет', 'Пока', 'геолог', '1234')bot = telebot.TeleBot('1317266508:AAHVFrtK1R49JyAgMULGck_apVi3A7F19G0')@bot.message_handler(commands=['start'])def start_message(message):	bot.send_message(message.chat.id, 'Привет, ты написал мне /start')@bot.message_handler(content_types=['text'])def send_text(message):	if message.text.lower() == 'привет':		bot.send_message(message.chat.id, 'Привет!')	elif message.text.lower == 'пока':		bot.send_message(message.chat.id, 'Прощай!')	elif message.text.lower() == 'геолог':		a = input(bot.send_message(message.chat.id, 'Назовите пароль?'))		while a == 1234:			if a == 1234:				bot.send_message(message.chat.id, 'Пароль верный')				bot.send_message(message.chat.id, 'Напишите по этому номеру +7 777 154 24 55')				break			bot.send_message(message.chat.id, 'Пароль неверный')	else:		bot.send_message(message.chat.id, 'Можете, повторить?')bot.polling(none_stop = True)